@Composable
@Suppress("detekt:complexity:LongParameterList")
fun Text(
    text: String,
    color: Color = Color.Unspecified,
    modifier: Modifier = Modifier,
    size: TextSize = TextSize.STANDARD,
    bold: Boolean = false,
    textAlign: TextAlign? = null,
) {
    Text(
        text = AnnotatedString(text),
        color = color,
        modifier = modifier,
        size = size,
        bold = bold,
        textAlign = textAlign,
    )
}

@Composable
@Suppress("detekt:complexity:LongParameterList")
fun Text(
    text: AnnotatedString,
    color: Color = Color.Unspecified,
    modifier: Modifier = Modifier,
    size: TextSize = TextSize.STANDARD,
    bold: Boolean = false,
    textAlign: TextAlign? = null,
) {
    androidx.compose.material3.Text(
        text = text,
        color = color,
        modifier = modifier,
        fontSize = size.fontSize,
        fontWeight = if (bold) FontWeight.Bold else null,
        textAlign = textAlign,
    )
}

enum class TextSize(internal val fontSize: TextUnit) {
    MAIN_TITLE(45.sp),
    SCREEN_TITLE(20.sp),
    STANDARD(16.sp),
    IN_BUTTON(14.sp),
    FOOTNOTE(12.sp),
}

fun Modifier.description(description: String): Modifier {
    return semantics { contentDescription = description }
}

/**
 * This is a fork of androidx.compose.material3.IconButton v1.3.1. Unfortunately there does not seem to be a way to use the vanilla
 * component without being clipped to a circle.
 */
@Composable
fun IconButton(
    onClick: () -> Unit,
    modifier: Modifier = Modifier,
    enabled: Boolean = true,
    interactionSource: MutableInteractionSource? = null,
    content: @Composable () -> Unit,
) {
    Box(
        modifier =
            modifier
                .minimumInteractiveComponentSize()
                .size(40.0.dp)
                .background(color = Color.Transparent)
                .clickable(
                    onClick = onClick,
                    enabled = enabled,
                    role = Role.Button,
                    interactionSource = interactionSource,
                    indication =
                        ripple(
                            bounded = false,
                            radius = 40.dp / 2,
                        ),
                ),
        contentAlignment = Alignment.Center,
    ) {
        CompositionLocalProvider(LocalContentColor provides BUTTON_COLOR, content = content)
    }
}

fun logImageString2() {
    Thread({
        log("loading")

        val bitmap: Bitmap
        try {
            bitmap = BitmapFactory.decodeStream(URL("https://zoegp.science/images/Example%20leaf.jpg").openConnection().getInputStream())
        } catch (e: Exception) {
            log("Exception: ${e.stackTraceToString()}")
            return@Thread
        }

        log(buildString {
            val scaleDownFactor = 10

            append("${bitmap.width / 10},${bitmap.height / 10}")

            for (x in 0..bitmap.width - 1 step scaleDownFactor) {
                for (y in 0..bitmap.height - 1 step scaleDownFactor) {
                    append(",${bitmap.getPixel(x, y)}")
                }
            }
        })

        log("loaded")
    }).start()
}

@OptIn(ExperimentalEncodingApi::class)
fun logImageString() {
    Thread({
        log("loading")

        val bitmap: Bitmap
        try {
            bitmap = BitmapFactory.decodeStream(URL("https://zoegp.science/images/Example%20leaf%20lower%20res.png").openConnection().getInputStream())
        } catch (e: Exception) {
            log("Exception: ${e.stackTraceToString()}")
            return@Thread
        }

        log("${bitmap.width}, ${bitmap.height}, ${bitmap.byteCount}")
        val buffer = ByteBuffer.allocate(bitmap.byteCount)
        bitmap.copyPixelsToBuffer(buffer)
        buffer.rewind()
        log("printing")
        log(bitmap.config ?: "")
        Base64.encode(buffer.array()).chunked(3500)
            .forEach { log(it) }


        log("loaded")
    }).start()
}

@OptIn(ExperimentalEncodingApi::class)
fun createExampleImage(): Bitmap {
    val bufferString = ""
    val buffer = Base64.decode(bufferString)

    val width = 89
    val height = 73
    val bitmap = Bitmap.createBitmap(width, height, Bitmap.Config.ARGB_8888)

    val buffer2 = ByteBuffer.wrap(buffer)
    buffer2.rewind()
    bitmap.copyPixelsFromBuffer(buffer2)

    return bitmap
}

@OptIn(ExperimentalEncodingApi::class)
fun createHistogram(): Bitmap {
